methods.moveForward:
servoLeft.writeMicroseconds(1700);
servoRight.writeMicroseconds(1300);
delay(time);
servoLeft.writeMicroseconds(1500);
servoRight.writeMicroseconds(1500);
delay(5)

methods.moveBackward:
servoLeft.writeMicroseconds(1300);
servoRight.writeMicroseconds(1700);
delay(time);
servoLeft.writeMicroseconds(1500);
servoRight.writeMicroseconds(1500);
delay(5)

methods.turnLeft:
servoLeft.writeMicroseconds(1300);
servoRight.writeMicroseconds(1300);
delay(degrees*5);
servoLeft.writeMicroseconds(1500);
servoRight.writeMicroseconds(1500);
delay(5)

methods.turnRight:
servoLeft.writeMicroseconds(1700);
servoRight.writeMicroseconds(1700);
delay(degrees*5);
servoLeft.writeMicroseconds(1500);
servoRight.writeMicroseconds(1500);
delay(5)

methods.InfraredSensor:
tone(pout, 38000, 8);
delay(1);
int ir = digitalRead(pin);
return !(ir)

metamethods.ReadSensors:
touchingLeft = WhiskerSensor(LeftWhisker);
touchingRight = WhiskerSensor(RightWhisker);
delay(10);
seeingLeft = InfraredSensor(LeftInfraredSensor, LeftInfraredEmitter);
delay(10);
seeingRight = InfraredSensor(RightInfraredSensor, RightInfraredEmitter)
